package JavaBasics.Automation;

public class Seriesdisplay {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		System.out.println("* Pattern is");
		for (int i=0;i<=n;i++)
		{
			for (int j=0;j<=i;j++)
			
				System.out.print( " " +j );
			System.out.println();
			}
		}
	}


